INSERT INTO `plugin_pages_pages` (`name_tag`, `title`, `content`, `publish`, `layout_id`, `category_id`, `x_robots_tag`) VALUES ('pay-online.html', 'Pay Online', '<h6><span style=\"font-size:20px\">Make a Payment Online</span></h6>\r\n\r\n<p><span style=\"font-size:12px\">Thank you for choosing to pay online. Please find our Quick Payment Form below. Please ensure that all details are entered correctly, as payment cannot be processed otherwise. Once payment is complete, you will receive a confirmation email. (If you do not receive it, it could be in your Spam Folder.)</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Please note that we do not accept payment for Grinds or any Pay as You Go Courses over our website.</span></p>\r\n\r\n<h6><span style=\"font-size:20px\">Your Payment is Secure</span></h6>\r\n\r\n<p>This page is a secure payment page with an SSL certificate installed to encrypt your payment details. </p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/media/photos/content/comodo-secure-icon.png\" style=\"height:73px; width:100px\" /> </p>\r\n\r\n<h6><span style=\"font-size:20px\">Having Trouble With Your Payment?</span></h6>\r\n\r\n<p><span style=\"font-size:12px\">If you have any issues with payment, please send an email to \'info@kes.ie\' with details of any error messages that appear on screen. Alternatively, you can call 061-444989 and one of our administration staff will be happy to deal with your query.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Please note: If you receive the error message \'Do Not Honor\', this is an error from your bank. Please get in touch with your bank to resolve this issue.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">You can contact the office for more information on any of the above points.</span></p>\r\n\r\n<p> </p>\r\n\r\n<h1 style=\"text-align:center\">Quick Payment Form</h1>\r\n', '1', '2', '1', 'noindex,nofollow');
