UPDATE IGNORE `ppages`
SET `content` = '<div class=\"registration\"> <h2>Log in</h2>  <p>Please enter your <strong>email address</strong> and <strong>password</strong> below to access the system</p>  <form action=\"frontend/users/login\" enctype=\"multipart/form-data\" id=\"login_form\" method=\"POST\"> <div class=\"register-row\"><label for=\"login_email\">Email</label> <input class=\"validate[required,custom[email]]\" id=\"login_email\" name=\"email\" placeholder=\"Enter your email address\" type=\"text\" /></div>  <div class=\"register-row\"><label for=\"login_password\">Password</label> <input class=\"validate[required]\" id=\"login_password\" name=\"password\" placeholder=\"Enter your password\" type=\"password\" /></div> <input id=\"login_stay_signed_in\" name=\"stay_signed_in\" type=\"checkbox\" /> <label for=\"login_stay_signed_in\">Keep me signed in</label>  <div class=\"register-row\"><input class=\"button button-primary\" id=\"login_button\" type=\"submit\" value=\"Log in\" /></div> </form>  <p>Don&#39;t have an account? <a href=\"/register-account.html\" title=\"Register Account\">Create one now</a></p> </div> '
WHERE `name_tag` = 'login.html';

