SET `content` = '<div class=\"registration\"> <h2>Register Account</h2>  <p class=\"register-note\">If you already have an account with us, please login at the <a href=\"/login.html\">login page</a>.</p>  <form action=\"frontend/formprocessor/\" class=\"formrt\" id=\"account_registration_form\" method=\"post\">{form-Register Account}</form>  <p><strong>Please note you will receive an email to verify your account upon registration.</strong></p> </div> '
WHERE `name_tag` = 'register-account.html';


